mapper.select:
SELECT r.role_id, r.role_name, m.menu_id, m.menu_icon, m.menu_name, m.menu_url, m.parent_id, m.sort_id, (SELECT COUNT(*) FROM aoa_sys_menu s WHERE s.parent_id=m.menu_id) childCount FROM aoa_role_power_list p INNER JOIN aoa_role_ r ON p.role_id=r.role_id INNER JOIN aoa_sys_menu m ON p.menu_id=m.menu_id
p.is_show=1
AND r.role_id = #{roleId}
AND m.parent_id = #{parentId}
ORDER BY m.sort_id

SELECT menu_id, is_show, menu_icon, menu_name, menu_url, parent_id, sort_id, menu_grade FROM aoa_sys_menu
menu_name LIKE '%${keyword}%'
ORDER BY sort_id LIMIT #{page}, #{count}

mapper.update:
UPDATE aoa_sys_menu SET isShow = #{isShow}, menuIcon = #{menuIcon},
menuName = #{menuName}, menUrl = #{menuUrl},parentId = #{parentId},
sortId = #{sortId}, menuGrade = #{menuGrade} WHERE menu_id = #{menuId}
